css:
 @media (min-width: 576px){
.header__logo {
    max-width: 210px;
    max-height: 60px;
}

.fa-search:before {
    content: "\e80a";
    font-size: 17px;
   }}
@media (min-width: 768px){
.header__search-input {
    background: #f1f1f1;
    height: 37px !important;
    font-size: 16px !important;
  }}

.glide__bullets {
display:none
}
.top-bar__nav a:last-child{
font-weight: 300;
}

@media (max-width: 500px)
.fa-search:before {
    content: "\e80a";
    font-size: 17px;
    color: white;
}
.product-card_prices .pricesvalue:first-child, .product-card_name {
    text-align: center;
}
.products-carousel__title a, .products-carousel__title span{
  font-size: 24px !important;
}
