css:
 @media (min-width: 576px){
.header__logo {
    max-width: 210px;
    max-height: 60px;
   }}


.prices__value{
  color: #a07671;
}
.minicart__btn-checkout{
   background: #a07671;
    text-transform: uppercase;
    font-weight: 700;
   }
.app__contact span:not(:last-child){
   color: white;
   }

.share{
  display: none;
 }
.variations__grid-title h5{
  font-size:16px;
   }

.fa-search:before {
    content: "\e80a";
    font-size: 17px;
   }}
@media (min-width: 768px){
.header__search-input {
    background: #f1f1f1;
    height: 37px !important;
    font-size: 16px !important;
  }}

.glide__bullets {
display:none
}
.top-bar__nav a:last-child{
font-weight: 300;
}

@media (max-width: 500px)
.fa-search:before {
    content: "\e80a";
    font-size: 17px;
    color: white;
}
.prices .product-card__prices{
  text-align: center !important;
}
.products-carousel__title a, .products-carousel__title span{
  font-size: 24px !important;
}
.product__name{
      font-size: 21px;
    font-weight: 400;
}
.prices--big .prices__value{
  font-size:25px;
}
.product__buy .btn {
  width: 100%;
  background: #a07671;
  text-transform: uppercase;
}
.product__short-stock{
  display: none !important;
}
.header__nav {
  font-size: 13px !important;
}
@media (min-width: 992px){
.gallery__item--video {
  background: #c58383;
    color: white;
  }}

.app__header{
  background: #a07671 !important;
  color: white;
}

.cart__btn-checkout .btn .btn-lg .btn-block .btn-success{
  text-transform: uppercase;
    background: #a07671 !important;
    border: none;
    border-radius: 0px;
}
#titulo-instagram{
   font-size: 20px;
   color: #a07671; 
}
.app__contact span:not(:first-child){
  color: white;
  font-weight: 700;
}
.product-card:hover .product-card__buy{
   display: none !important;
}
